const cds = require('../../../../lib/cds')

module.exports = class extends require('../../plugin') {

    static help() {
      return 'Kyma deployment using Helm charts'
    }

    options() {
      return {
        'unified-runtime': {
          type: 'boolean'
        }
      }
    }

    requires() {
      if (cds.cli.options['unified-runtime']) {
        return ['helm-unified-runtime', 'containerize']
      }
      return ['helm', 'containerize']
    }
}
